import React, { useState } from 'react';
import './App.css';
import Home from './pages/Home';
import PrivacyPolicy from './pages/PrivacyPolicy';
import Nav from './components/Nav';

function App() {
  // State to track if the user is connected
  const [isConnected, setIsConnected] = useState(false);

  // Mock user profile data
  const userProfile = {
    name: 'John Doe',
    image: 'https://via.placeholder.com/40',
  };

  return (
    <div className="App">
      {/* Render Nav with conditional logic based on isConnected */}
      {isConnected ? (
        <Nav userProfile={userProfile} />
      ) : (
        <Nav />
      )}

      {/* Other components like Home and PrivacyPolicy */}
      <Home />
      <PrivacyPolicy />

      {/* Button to simulate login/logout */}
      <button onClick={() => setIsConnected(!isConnected)}>
        {isConnected ? 'Logout' : 'Login'}
      </button>
    </div>
  );
}

export default App;
