import React, { useState, useEffect } from 'react';
import Nav from "../components/Nav";
import './Dashboard.css';
import Chart from 'chart.js/auto'; // Import Chart from the chart.js library

function Dashboard() {
    const [isConnected, setIsConnected] = useState(true);
    const [searchQuery, setSearchQuery] = useState(""); // State to store search query

    const userProfile = {
      name: 'John Doe',
      image: 'https://via.placeholder.com/40',
    };

    const handleLogout = () => {
      setIsConnected(false);
    };

    const handleSearch = (event) => {
        setSearchQuery(event.target.value);
        // Add the logic here to filter patients based on searchQuery
    };

    return (
        <div className="dashboard-page">
            <Nav userProfile={isConnected ? userProfile : null} handleLogout={handleLogout} />

            <div className="dashboard-content">
                <h1>Welcome, {userProfile.name}!</h1>

                {/* Search Bar */}
                <div className="search-bar">
                    <input 
                        type="text" 
                        placeholder="Search for patients..." 
                        value={searchQuery} 
                        onChange={handleSearch} 
                    />
                </div>
                
                <div className="dashboard-section">
                    <div className="chart-container">
                        <h2>Recent Patients</h2>
                        <canvas id="patientsChart"></canvas>
                    </div>
                    <div className="chart-container">
                        <h2>Recent Diagnoses</h2>
                        <canvas id="diagnosesChart"></canvas>
                    </div>
                </div>

                <div className="dashboard-buttons">
                    <button className="dashboard-button">View Diagnosis History</button>
                    <button className="dashboard-button">New Diagnosis</button>
                    <button className="dashboard-button">Update Profile</button>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
