import React, { useState } from 'react';
import Nav from "../components/Nav";
import './Dashboard.css';
import { Bar } from 'react-chartjs-2'; // Import Bar chart from react-chartjs-2
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

// Register required components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard() {
    const [isConnected, setIsConnected] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");

    const userProfile = {
      name: 'John Doe',
      image: 'https://via.placeholder.com/40',
    };

    const handleLogout = () => {
        setIsConnected(false);
    };

    const handleSearch = (event) => {
        setSearchQuery(event.target.value);
    };

    const mockData = [
        { date: '2024-09-01', patients: 10, diagnoses: 5 },
        { date: '2024-09-02', patients: 8, diagnoses: 3 },
        { date: '2024-09-03', patients: 12, diagnoses: 6 },
    ];

    const generateChartData = (patients, diagnoses) => {
        return {
            labels: ['Patients', 'Diagnoses'],
            datasets: [
                {
                    label: 'Count',
                    data: [patients, diagnoses],
                    backgroundColor: ['#4CAF50', '#FFC107'],
                },
            ],
        };
    };

    return (
        <div className="dashboard-page">
            <Nav userProfile={isConnected ? userProfile : null} handleLogout={handleLogout} />

            <div className="dashboard-content">
                <h1>Welcome, Dr {userProfile.name}!</h1>

                <div className="search-bar">
                    <input 
                        type="text" 
                        placeholder="Search for patients..." 
                        value={searchQuery} 
                        onChange={handleSearch} 
                    />
                </div>

                <div className="calendar-grid">
                    {mockData.map((data) => (
                        <div
                            key={data.date}
                            className="calendar-day"
                        >
                            <p>{data.date}</p>
                            <Bar 
                                data={generateChartData(data.patients, data.diagnoses)} 
                                options={{ responsive: true, maintainAspectRatio: false }} 
                            />
                        </div>
                    ))}
                </div>

                <div className="dashboard-buttons">
                    <button className="dashboard-button">View Diagnosis History</button>
                    <button className="dashboard-button">New Diagnosis</button>
                    <button className="dashboard-button">Update Profile</button>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
