import './ContentCard.css';
const ContentCard = ({ title, description, buttonText, buttonLink, image, imagePosition = "left" }) => {
    return (
      <div className={`content-card ${imagePosition === "left" ? "image-left" : "image-right"}`}>
        {/* Image Section */}
        <div className="content-image">
          <img src={image} alt={title} />
        </div>
  
        {/* Text Section */}
        <div className="content-text">
          <h2>{title}</h2>
          <p>{description}</p>
          <button onClick={() => window.location.href = buttonLink} className="content-button">
            {buttonText}
          </button>
        </div>
      </div>
    );
  };

export default ContentCard;
