import React, { useState } from 'react';
import Nav from "../components/Nav";
import './Dashboard.css';
// Import necessary libraries for charts if needed
// import { Bar } from 'react-chartjs-2'; // Example with Chart.js
import { Bar } from 'react-chartjs-2';

const recentPatientsData = {
  labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
  datasets: [
    {
      label: 'Number of Patients',
      backgroundColor: '#32cd32',
      data: [12, 19, 3, 5, 2, 3],
    },
  ],
};

const recentDiagnosesData = {
  labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
  datasets: [
    {
      label: 'Number of Diagnoses',
      backgroundColor: '#007bff',
      data: [2, 3, 20, 5, 1, 4],
    },
  ],
};

function Dashboard() {
    const [isConnected, setIsConnected] = useState(true);

    const userProfile = {
      name: 'John Doe',
      image: 'https://via.placeholder.com/40',
    };

    const handleLogout = () => {
      setIsConnected(false);
    };

    // Placeholder data for charts
    //const recentPatientsData = {}; // Define your chart data here
    //const recentDiagnosesData = {}; // Define your chart data here

    return (
    <div className="dashboard-page">
            <Nav userProfile={isConnected ? userProfile : null} handleLogout={handleLogout} />




                 {/* Greeting and Search Bar */}
      <div className="dashboard-header">
        <h1>Welcome, {userProfile.name}!</h1>
        <input
          type="text"
          placeholder="Search for patients"
          className="search-bar"
        />
      </div>

      {/* Recent Patients and Diagnoses Charts */}
      <div className="charts-container">
      <div className="chart">
  <h2>Recent Patients</h2>
  <Bar data={recentPatientsData} options={{ maintainAspectRatio: false }} />
</div>
<div className="chart">
  <h2>Recent Diagnoses</h2>
  <Bar data={recentDiagnosesData} options={{ maintainAspectRatio: false }} />
</div>

      </div>

      {/* Action Buttons */}
      <div className="dashboard-actions">
        <button className="btn-primary">View Diagnosis History</button>
        <button className="btn-primary">New Diagnosis</button>
        <button className="btn-primary">Update Profile</button>
      </div>
    </div>
    );
}

export default Dashboard;
